import AWS from "aws-sdk";

const dynamoDb = new AWS.DynamoDB.DocumentClient();

export const main = async (event) => {

  const params = {
    TableName: process.env.productsTabledynamodb,

  };

  try {
    const list = await dynamoDb.scan(params).promise();
    console.log(list);
    return {
      statusCode: 200,
      body: JSON.stringify({
